#%%
from block import Block
from setting import *
from balloon import Balloon
from player import Player
from level_data import *
#%%
class Level:
    def __init__(self,screen,asset):
        self.screen=screen
        self.asset=asset
        self.background_images=self.asset.background_images
        self.foreground_images=self.asset.foreground_images
        self.game_status_signal=self.asset.game_status_signal
        self.level_data=Level_Data()
        
        self.level=2
        self.background_image=self.background_images[self.level-1]
        self.foreground_image=self.foreground_images[self.level-1]
        
        self.timer=0
        
        self.status='ready'
        self.game_status_signal=self.game_status_signal[self.status]
        self.game_status_signal_rect=self.game_status_signal.get_rect(center=(stage_width//2,stage_height//2))
        self.game_status_timer=0
        # self.level_data()
    
    def levels(self):
        self.balloons=pygame.sprite.Group()
        self.blocks=pygame.sprite.Group()
        self.player=pygame.sprite.GroupSingle()
        self.update_time=pygame.time.get_ticks()
        if self.level==1:
            self.time=100
            self.background_image=self.background_images[self.level-1]
            for row_index,column in enumerate(self.level_data.level['1']):
                for column_index,value in enumerate(column):
                    x=column_index*24
                    y=row_index*24
                    if value=='b':
                        self.balloon1=Balloon(self.asset,'red',0,(x,y),True,False)
                        self.balloons.add(self.balloon1)
                    if value=='p':
                        self.player.add(Player(self.screen,self.asset,x,y))
                    if value=='bk1':
                        self.blue_block1=Block(self.asset,x,y,'blue','size4')
                        self.blocks.add(self.blue_block1)
        elif self.level==2:
            self.time=110
            self.background_image=self.background_images[self.level-1]
            self.foreground_image=self.foreground_images[self.level-1]
            for row_index,column in enumerate(self.level_data.level['2']):
                for column_index,value in enumerate(column):
                    x=column_index*24
                    y=row_index*24
                    if value=='b':
                        self.balloon1=Balloon(self.asset,'red',0,(x,y),True,False)
                        self.balloons.add(self.balloon1)
                    if value=='p':
                        self.player.add(Player(self.screen,self.asset,x,y))
                    if value=='bk4':
                        self.blue_block1=Block(self.asset,x,y,'blue','size4')
                        self.blocks.add(self.blue_block1)
    
